<script>
const q={gross:<?= json_encode((float)$calc['gross_payable']) ?>,subsidy:<?= json_encode((float)$calc['subsidy_expected_rs']) ?>,monthly:<?= json_encode((float)($quote['finance_inputs']['monthly_bill_rs'] ?? 0)) ?>,unit:<?= json_encode((float)($quote['finance_inputs']['unit_rate_rs_per_kwh'] ?: $segmentDefaults['unit_rate_rs_per_kwh'] ?? 8)) ?>,cap:<?= json_encode((float)($quote['capacity_kwp'] ?? 0)) ?>,gen:<?= json_encode((float)($quote['finance_inputs']['annual_generation_per_kw'] ?: $segmentDefaults['annual_generation_per_kw'] ?? $quoteDefaults['global']['energy_defaults']['annual_generation_per_kw'] ?? 1450)) ?>};
const r=x=>'â‚¹'+Number(x).toLocaleString('en-IN',{maximumFractionDigits:0});
const minMargin=q.gross*0.10,loan=Math.min(q.gross-minMargin,200000),margin=q.gross-loan,loanEff=Math.max(0,loan-q.subsidy),rr=0.06/12,n=120,emi=loanEff>0?(loanEff*rr*Math.pow(1+rr,n))/((Math.pow(1+rr,n))-1):0,mUnits=q.monthly/Math.max(0.1,q.unit),solar=(q.cap*q.gen)/12,res=Math.max(0,mUnits-solar)*q.unit,out=emi+res;
document.getElementById('margin').textContent=r(margin);document.getElementById('loan').textContent=r(loan);document.getElementById('loanEff').textContent=r(loanEff);document.getElementById('emi').textContent=r(emi);document.getElementById('residual').textContent=r(res);document.getElementById('outflow').textContent=r(out);document.getElementById('upfront').textContent=r(q.gross);document.getElementById('upfrontNet').textContent=r(q.gross-q.subsidy);document.getElementById('selfResidual').textContent=r(res);document.getElementById('noSolar').textContent='Monthly bill: '+r(q.monthly);
function bars(){const c=document.getElementById('c1').getContext('2d'),v=[q.monthly,out,res],cl=['#ef4444','#2563eb','#16a34a'];c.clearRect(0,0,900,220);const m=Math.max(...v,1);v.forEach((x,i)=>{c.fillStyle=cl[i];c.fillRect(40+i*250,180-(x/m*140),80,x/m*140);});}bars();
function lines(){const c=document.getElementById('c2').getContext('2d');c.clearRect(0,0,900,220);const pts=[];for(let y=0;y<=10;y++){pts.push([y,q.monthly*y*12,out*y*12,q.gross+res*y*12]);}const m=Math.max(...pts.map(p=>Math.max(p[1],p[2],p[3])));[['#ef4444',1],['#2563eb',2],['#16a34a',3]].forEach(a=>{c.strokeStyle=a[0];c.beginPath();pts.forEach((p,i)=>{const x=40+p[0]*80,yy=190-(p[a[1]]/m*150);if(i===0)c.moveTo(x,yy);else c.lineTo(x,yy)});c.stroke();});}lines();
const sav=(q.monthly-res)*12;document.getElementById('payback').textContent='Payback meter: ~'+(q.gross/Math.max(1,sav)).toFixed(1)+' years';
const yearly=q.cap*q.gen,co2=yearly*0.82,tree=co2/20;document.getElementById('co2y').textContent=co2.toFixed(0)+' kg';document.getElementById('treey').textContent=tree.toFixed(1);document.getElementById('co225').textContent=(co2*25).toFixed(0)+' kg';document.getElementById('tree25').textContent=(tree*25).toFixed(1);
</script>
